INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (1, 'ban-ur', 'Ban Ur', 'Ban-Ur is the tribal homeland of the nomadic Banuk tribe.', 'Tribal Territory', 'Banuk', 'ban-ur.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (2, 'clan-lands', 'Clan Lands', 'The Clan Lands, also known as the Tenakth Lands, is the territory belonging to the Tenakth tribe within the Forbidden West. The Clan Lands make up the majority of the Forbidden West.', 'Tribal Territory', 'Tenakth', 'clan-lands.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (3, 'great-delta', 'Great Delta', 'The Great Delta is the mentioned-only homeland of the Quen tribe.', 'Tribal Territory', 'Quen', 'great-delta.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (4, 'plainsong', 'Plainsong', 'Plainsong is the home territory of the Utaru tribe.', 'Tribal Territory', 'Utaru', 'plainsong.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (5, 'sacred-lands', 'Sacred Lands', 'The Sacred Lands, also known as Nora Territory or the Savage East, is the territory belonging to the people of the Nora tribe.', 'Tribal Territory', 'Nora', 'sacred-lands.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (6, 'sundom', 'Sundom', 'The Sundom (portmanteau of Sun and Kingdom) is the territory of the Carja tribe, featured in Horizon Zero Dawn and Horizon Forbidden West.', 'Tribal Territory', 'Carja', 'sundom.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (7, 'the-claim', 'The Claim', 'The Claim is a mentioned-only location in Horizon Zero Dawn and Horizon Forbidden West. It is the tribal territory of the Oseram.', 'Tribal Territory', 'Oseram', 'the-claim.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (8, 'forbidden-west', 'Forbidden West', 'The Forbidden West is the Carja name given to the land west of the Daunt. This uncharted region is known for its mystery and danger, as few people who venture there have ever returned in one piece.', 'Primary Location', 'Unknown', 'forbidden-west.png');
INSERT INTO horizonapi.area (id, slug, name, description, type, faction, image_path) VALUES (9, 'burning-shores', 'Burning Shores', 'The Burning Shores are the playable location of Horizon Forbidden West: Burning Shores. The Burning Shores were mentioned in datapoints in Horizon Forbidden West.', 'Primary Location', 'None', 'burning-shores.png');
