INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (1, 'gatelands', 'Gatelands', 'The Gatelands is a region in the eastern and central parts of the Carja Sundom in Horizon Zero Dawn.', 6, 'gatelands.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (2, 'rustwash', 'Rustwash', 'The Rustwash is the northwestern region of the Sundom in Horizon Zero Dawn.', 6, 'rustwash.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (3, 'sun-furrows', 'Sun Furrows', 'Sun Furrows is a northern and central region of the Carja Sundom in Horizon Zero Dawn. It shares its western border with the Rustwash region and the Daybrink.', 6, 'sun-furrows.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (4, 'sun-steps', 'Sun-Steps', 'The Sun-Steps are the northeasternmost region of the Carja Sundom in Horizon Zero Dawn.', 6, 'sun-steps.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (5, 'the-cut', 'The Cut', 'The Cut is the name that the Banuk use to refer to the region due south of Ban-Ur, that was visited by the Nora Aloy in Horizon Zero Dawn: The Frozen Wilds.', 1, 'the-cut.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (6, 'the-embrace', 'The Embrace', 'The Embrace, sometimes called All-Mother''s Embrace, is a small valley region in the southwestern part of the Nora Sacred Lands in Horizon Zero Dawn.', 5, 'the-embrace.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (7, 'the-jewel', 'The Jewel', 'The Jewel is the southern region of the Carja Sundom in Horizon Zero Dawn.', 6, 'the-jewel.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (8, 'the-longroam', 'The Longroam', 'The Longroam is the region to the north of the Nora Sacred Lands in Horizon Zero Dawn. It is a very mountainous region, with high peaks surrounding three valleys, one of which contains the Banuk Encampment.', 5, 'the-longroam.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (9, 'valleymeet', 'Valleymeet', 'The Valleymeet is a region in Horizon Zero Dawn north of Mother''s Crown. It is so-named because a number of valleys north of the Sacred Lands join here.', 5, 'valleymeet.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (10, 'bonewhite-tear', 'Bonewhite Tear', 'Bonewhite Tear is a region in Horizon Forbidden West.', 2, 'bonewhite-tear.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (11, 'burning-shores', 'Burning Shores', 'The Burning Shores are the playable location of Horizon Forbidden West: Burning Shores. The Burning Shores were mentioned in datapoints in Horizon Forbidden West.', 9, 'burning-shores.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (12, 'cliffs-of-the-cry', 'Cliffs of the Cry', 'Cliffs of the Cry is a region in Horizon Forbidden West.', 2, 'cliffs-of-the-cry.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (13, 'dry-yearn', 'Dry Yearn', 'The Dry Yearn is a region in Horizon Forbidden West.', 2, 'dry-yearn.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (14, 'greenswell', 'Greenswell', 'The Greenswell is a region in Horizon Forbidden West.', 2, 'greenswell.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (15, 'isle-of-spires', 'Isle of Spires', 'The Isle of Spires, also referred to as the Sunken City, is a region in Horizon Forbidden West.', 8, 'isle-of-spires.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (16, 'no-mans-land', 'No Man''s Land', 'No Man''s Land is a region in Horizon Forbidden West. Similar to The Cut and Valleymeet, it acts as a buffer zone between territories, in this case between the Tenakth Clan Lands and the Carja Sundom.', 10, 'no-mans-land.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (17, 'the-raintrace', 'The Raintrace', 'The Raintrace is a region in Horizon Forbidden West, home to the Tenakth Lowland Clan.', 2, 'the-raintrace.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (18, 'restless-weald', 'Restless Weald', 'Restless Weald is a region in Horizon Forbidden West.', 4, 'restless-weald.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (19, 'runners-wild', 'Runner''s Wild', 'Runner''s Wild is a region in Horizon Forbidden West, located north of the Memorial Grove and south of Salt Bite.', 2, 'runners-wild.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (20, 'sheerside-mountains', 'Sheerside Mountains', 'The Sheerside Mountains are a region in the Clan Lands.', 2, 'sheerside-mountains.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (21, 'shining-wastes', 'Shining Wastes', 'The Shining Wastes is a region in Horizon Forbidden West.', 2, 'shining-wastes.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (22, 'stand-of-the-sentinels', 'Stand of the Sentinels', 'The Stand of the Sentinels is a region in the Forbidden West, located to the west of the Sheerside Mountains and north of the Raintrace.', 2, 'stand-of-the-sentinels.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (23, 'stillsands', 'Stillsands', 'The Stillsands is a region in Horizon Forbidden West located south of the Shining Wastes and east of the Raintrace.', 10, 'stillsands.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (24, 'the-daunt', 'The Daunt', 'The Daunt is a Sundom region in Horizon Forbidden West.', 6, 'the-daunt.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (26, 'salt-bite', 'Salt Bite', 'Salt Bite is a Desert Tenakth settlement in Horizon Forbidden West.', 2, 'salt-bite.png');
INSERT INTO horizonapi.region (id, slug, name, description, id_area, image_path) VALUES (25, '21st-century-facility', '21st Century Facility', '', 11, '21-century.png');
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (1, 'sigma', 'Cauldron SIGMA', 'Cauldron SIGMA is a HEPHAESTUS facility that manufactures machines for use in terraforming.', 'cauldron-sigma.png', 'sigma-location.png', 9);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (2, 'rho', 'Caudron RHO', 'Cauldron RHO is a HEPHAESTUS facility that manufactures machines for use in terraforming. The only known machine that RHO produces are Snapmaws.', 'cauldron-rho.png', 'rho-location.png', 1);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (3, 'xi', 'Cauldron XI', 'Cauldron XI is an activity in Horizon Zero Dawn. It was a HEPHAESTUS facility that manufactured machines for use in terraforming before collapsing.', 'cauldron-xi.png', 'xi-location.png', 7);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (4, 'zeta', 'Cauldron ZETA', 'Cauldron ZETA is an activity in Horizon Zero Dawn. It is a HEPHAESTUS facility that manufactures machines for use in terraforming. The only known machine that ZETA produces are Thunderjaws.', 'cauldron-zeta.png', 'zeta-location.png', 3);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (5, 'epsilon', 'Cauldron EPSILON', 'Cauldron EPSILON was a location in Horizon Zero Dawn: The Frozen Wilds, located within the 21st century facility known as Firebreak.', 'cauldron-epsilon.png', 'epsilon-location.png', 11);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (6, 'psi', 'Cauldron PSI', 'Cauldron PSI is a mentioned-only Cauldron. While Cauldron PSI is never visited by Aloy, she gains access to the PSI overrides when she takes the override module from a Corruptor.', 'cauldron-psi.png', 'psi-location.png', 25);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (7, 'repair-bay-tau', 'Repair-Bay TAU', 'Repair-Bay TAU, known as the Sacred Cave to the Utaru, is a machine repair Cauldron in Horizon Forbidden West. It is located west of Plainsong and is directly connected to a facility above it in the mountain.', 'cauldron-repair-bay-tau.png', 'repair-bay-tau-location.png', 4);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (8, 'gemini', 'Cauldron GEMINI', 'Cauldron GEMINI is a Cauldron in Horizon Forbidden West.', 'cauldron-gemini.png', 'gemini-location.png', 21);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (9, 'mu', 'Cauldron MU', 'Cauldron MU is a Cauldron activity in Horizon Forbidden West.', 'cauldron-mu.png', 'mu-location.png', 16);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (10, 'iota', 'Cauldron IOTA', 'Cauldron IOTA is a Cauldron activity in Horizon Forbidden West.', 'cauldron-iota.png', 'iota-location.png', 26);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (11, 'kappa', 'Cauldron KAPPA', 'Cauldron KAPPA is a Cauldron activity in Horizon Forbidden West.', 'cauldron-kappa.png', 'kappa-location.png', 17);
INSERT INTO horizonapi.cauldron (id, slug, name, description, path_image, map_location, id_region) VALUES (12, 'chi', 'Cauldron CHI', 'Cauldron CHI is a Cauldron activity in Horizon Forbidden West.', 'cauldron-chi.png', 'chi-location.png', 17);
